// Remove the triple backticks and the "html" identifier
const rawContent = $json.text;
const cleanedHtml = rawContent.replace(/```html|```/g, '').trim();

return {
  html_content: cleanedHtml
};

// 1. Get the HTML string from the previous node (e.g., 'text' or 'content')
const htmlString = $input.all()
// 2. Create a binary buffer
const buffer = Buffer.from(htmlString[0].json.html, 'utf-8');
return {
    json: {
      converted: true
    },
    binary: {
      // 'data' is the standard field name for n8n binary data
      data: {
        // Use the helper to create the n8n binary structure
        data: buffer.toString('base64'), 
        mimeType: 'text/html',
        fileName: 'index.html',
        fileExtension: 'html'
      }
    }
}
